#!/usr/bin/perl
use strict;
use warnings;
use Storable;

BEGIN {
    my $dir = $0;
    $dir =~ s/\/[^\/]+$//;
    if ($dir !~ /^\//) {
        chomp(my $cwd = `pwd`);
        $dir = $cwd . "/" . $dir;
    }
    push @INC, $dir;
}

use Game;
use History;


my $outdir = '/tmp/sheldon/';
my $history_file = $outdir . "history.dat";

my $history = read_history($history_file);
my $game = $history->get_opponent($ARGV[0]) || new Game();

print make_play($game, @ARGV) . "\n";

if ($ARGV[1] eq 'finish') {
    $game->finished($ARGV[1]);
}

write_history($history_file, $history, $game);

sub make_play {
    my $game = shift;

    if ($game->play_count == 0 || $game->play_count >= length($game->used_pattern())) {
        $game->opponent($_[0]);
        $game->used_pattern(get_new_pattern($game));
    }

    $game->play((split//,$game->used_pattern())[$game->play_count()+1]);
    
    return $game->play();
}

sub read_history {
    my $history_file = shift;

    if (! -e $history_file) {
        open(FH, ">$history_file");
        close(FH);
        my $history = new History;
        store $history, $history_file;
    }

    return retrieve($history_file);
}

sub write_history {
    my $history_file = shift;
    my $history_obj = shift;    
    my $game = shift;
    $history_obj->store_opponent($game->opponent(), $game);
    store $history_obj, $history_file;
}

sub get_new_pattern {
    my $game = shift;
    my $new;
    do {
        local $/ = undef;
        $new = (split /\n/,<DATA>)[int((localtime())[0]/5)];
    } while ($new eq $game->used_pattern());
    return $new;  
}

